CREATE SCHEMA Stage;
CREATE TABLE Stage.airlines (Name varchar(255) NULL, IATA varchar(255) NULL, ICAO varchar(255) NULL, Callsign varchar(255) NULL, Country varchar(255) NULL, Active varchar(255) NULL, Alias varchar(255) NULL);
CREATE TABLE Stage.Airports (Name varchar(255) NULL, City varchar(255) NULL, Country varchar(255) NULL, IATA varchar(255) NULL, ICAO varchar(255) NULL, Latitude float(10) NULL, Longitude float(10) NULL, Altitude int NULL, Timezone varchar(255) NULL, DST varchar(255) NULL);
CREATE TABLE Stage.DelayAirport (IATA_CODE varchar(255) NULL, AIRPORT varchar(255) NULL, CITY varchar(255) NULL, STATE varchar(255) NULL, COUNTRY varchar(255) NULL, LATITUDE float(10) NULL, LONGITUDE float(10) NULL);
CREATE TABLE Stage.Flights (YEAR int NULL, MONTH int NULL, DAY int NULL, DAY_OF_WEEK int NULL, AIRLINE varchar(255) NULL, FLIGHT_NUMBER int NULL, TAIL_NUMBER varchar(255) NULL, ORIGIN_AIRPORT varchar(255) NULL, DESTINATION_AIRPORT varchar(255) NULL, SCHEDULED_DEPARTURE varchar(255) NULL, DEPARTURE_TIME varchar(255) NULL, DEPARTURE_DELAY varchar(255) NULL, TAXI_OUT int NULL, WHEELS_OFF varchar(255) NULL, SCHEDULED_TIME int NULL, ELAPSED_TIME int NULL, AIR_TIME int NULL, DISTANCE int NULL, WHEELS_ON varchar(255) NULL, TAXI_IN int NULL, SCHEDULED_ARRIVAL varchar(255) NULL, ARRIVAL_TIME varchar(255) NULL, ARRIVAL_DELAY int NULL, DIVERTED bit NULL, CANCELLED bit NULL, CANCELLATION_REASON varchar(255) NULL, AIR_SYSTEM_DELAY int NULL, SECURITY_DELAY int NULL, AIRLINE_DELAY int NULL, LATE_AIRCRAFT_DELAY int NULL, WEATHER_DELAY int NULL);